import React, { useEffect, useState } from 'react';
import { Text, TextInput, View,Button, StyleSheet,Alert, Pressable } from 'react-native';
import { useNavigation } from '@react-navigation/native';

import {useRoute} from '@react-navigation/native'

import SpHeader from '../components/header'
import SpPost from '../components/post'
import { getCurrentTimestamp } from 'react-native/Libraries/Utilities/createPerformanceLogger';
import { clickProps } from 'react-native-web/dist/cjs/modules/forwardedProps';



const styles = StyleSheet.create({
  post:{
      width: '100%',
      backgroundColor: '#C0E2FB',
      alignItems: 'center',
      padding: 5,
      borderRadius: 20,
      top: 20,
      marginBottom: 20,
      elevation: 20,
      shadowColor: '#52006A',
  },
  post1:{
      flexDirection: 'row',
      alignSelf: "baseline", 
      marginLeft: 10, 
  },
  image:{
      width: 40,
      height: 40,
      backgroundColor: 'white',
      borderRadius: 40,
      alignSelf: 'flex-start'
  },
  info:{
      marginLeft: 10,    
  },
  text: {
      fontWeight: 'bold',
  },
  post2:{
      alignSelf: 'baseline',
      marginLeft: 20,
      flexDirection: 'column',
      marginBottom: 15,
      marginTop: 15,
  },
  post3:{
      flexDirection: 'row',        
  },
  button:{
    borderWidth: 1,
    borderStyle: 'solid',
    borderColor: '#8DCACE',
    //boxSizing: 'border-box',
    borderRadius: 10,
    marginTop: 20,
    paddingHorizontal: '10%',
    paddingHorizontal: '10%', 
    alignSelf: 'center'
  },
  input:{
    shadowOffset: {width: -2, height: 4},
    shadowColor: 'rgba(0, 0, 0, 0.25)',
    shadowOpacity: 0.2,
    shadowRadius: 3,
    borderRadius: 10,
    backgroundColor: "white",
    width: '100%',
    height: '100%', 
    alignItems: 'center',
    marginBottom: '10%',
    paddingLeft: 30
  },
})

function add_post(id, token, textt){
  const xhttp = fetch('http://localhost:3333/api/1.0.0/user/'+id+'/post', {
      method: 'POST',
      headers: {
        'Accept': 'application/json',
        'Content-Type': 'application/json',
        'X-Authorization': token,
      },
      body: JSON.stringify({
          text: textt
        })
    })
    .then((response) => response.text())
    .then((text) => {     
        //console.log(text)                
    })
    .catch(function (res){
      console.log(res)
    });
}


function AddPost(props) {
  //const [info, setInfo] = useState({});

  const navigation = useNavigation();

  const [post,setPost] = useState({
    post_id: "",
    text: "",
    timestamp: getCurrentTimestamp(),
    author: {
        user_id: "",
        first_name: "",
        last_name: "",
        email: ""
    },
    numLikes: 0
  });
  const [text, setText] = useState("");


  const route = useRoute();

  //console.log("Params are" + route.params)
  var id = route.params.user;
  var postt = route.params.post;
  //console.log(post)
  //token = route.params.token;
  useEffect(() => {
    const abortController = new AbortController();


    const page = async () => {
      const xhttp = await fetch('http://localhost:3333/api/1.0.0/user/'+id+'/post/'+postt, {
                    method: 'GET',
                    headers: {
                      'Accept': 'application/json',
                      'Content-Type': 'application/json',
                      'X-Authorization': props.token,
                    }
                  })
                  .then((response) => response.json())
                  .then((text) => {                    
                      setPost(text);                
                      //console.log(post)
                  })
                  .catch(function (res){
                    console.log(res)
                  });

    }

    page();



    return function cleanup(){
      abortController.abort()
    }
  }, []);





    return (
      <View>

          <View style={styles.post}>
                <View style={styles.post1}>

                    <View style={styles.image}>            
                    </View>

                    <View style={styles.info}>
                        <Text style={styles.text}>
                          {props.first_name} {props.last_name}
                        </Text>

                    </View>

                </View>

                <View style={{
                    borderBottomColor: 'black',
                    borderBottomWidth: 1,
                    width: '100%',
                    paddingTop: 10,
                }}/>

                <View style={styles.post2}>

                    <Text style={styles.text}>
                        Add New Post 
                    </Text>                                    


                </View>                    

                <View style={styles.post3}>

                <TextInput
                        style={styles.input}
                        onChangeText={(text) => setText(text)}
                        //value={text}
                        placeholder="Enter New Text"
                        keyboardType='default'
                    />

                    </View>
                </View>


          <View>

          <Pressable style={styles.button}  onPress={() => {
            add_post(props.user, props.token, text)
            navigation.goBack() 
            }}>
            <Text style={styles.pressText}>Add</Text>
          </Pressable>

          <Pressable style={styles.button}  onPress={() => navigation.goBack()}>
            <Text style={styles.pressText}>Cancel</Text>
          </Pressable>

          </View>
      </View> 
    )};

export default AddPost;
